package org.hippoecm.frontend.plugins.cms.dev.updater

import org.onehippo.repository.update.BaseNodeUpdateVisitor
import javax.jcr.Node

class UpdaterTemplate extends BaseNodeUpdateVisitor {

  boolean doUpdate(Node node) {
    boolean result = false;
    if (node.isNodeType("hsl:ImageTeaser")) {
      
      log.debug("Analysing node: " + node.getPath());
          
      if(!node.isCheckedOut()){
        node.checkout();
      }
      
      if(node.hasNode("hsl:internallink")){
        Session session = node.getSession();
        session.move(node.getPath()+"/hsl:internallink", node.getPath()+"/hsl:link");
        log.debug("moved");
        result = true;
      }else{
        log.debug("No internal link found");
      }
    }
    return result;
  }

  boolean undoUpdate(Node node) {
    throw new UnsupportedOperationException('Updater does not implement undoUpdate method')
  }

}
