package org.hippoecm.frontend.plugins.cms.dev.updater

import org.onehippo.repository.update.BaseNodeUpdateVisitor
import javax.jcr.Node

class UpdaterTemplate extends BaseNodeUpdateVisitor {

  boolean doUpdate(Node node) throws RepositoryException {
      log.debug("Updating node ${node.path}");
      
      if(node.hasNodes()){
          NodeIterator nodeIterator = node.getNodes();
          while(nodeIterator.hasNext()){
              Node childNode = (Node) nodeIterator.next();
              if(childNode.getProperty("jcr:primaryType").getString().equals("hippostd:folder")){                  
                    setAllowedContent(childNode);
              }
          }
      }
      
      return false;
    }  
 
  void setAllowedContent(Node node) throws RepositoryException {
          
    String[] valueArray = new String[6];
          valueArray[0] = "new-education-page";
          valueArray[1] = "new-article-page";
          valueArray[2] = "new-event-page";
          valueArray[3] = "new-news-page";
          valueArray[4] = "new-faq-overview-page";
          valueArray[5] = "new-faq-folder";
    
          node.setProperty("hippostd:foldertype", valueArray);
      }

  boolean undoUpdate(Node node) {
    throw new UnsupportedOperationException('Updater does not implement undoUpdate method')
  }

}
