package org.hippoecm.frontend.plugins.cms.dev.updater

import org.onehippo.repository.update.BaseNodeUpdateVisitor
import javax.jcr.Node

class UpdaterTemplate extends BaseNodeUpdateVisitor {

  boolean doUpdate(Node node) throws RepositoryException {
        
        if(node.getPrimaryNodeType().getName().equals("hippo:handle")){
            addTranslationIdToDocument(node);
        }else if (node.getPrimaryNodeType().getName().equals("hippostd:folder")){
            String uuid = UUID.randomUUID().toString();
            node.setProperty("hippotranslation:id", uuid);
            log.debug("set translation uuid: " + uuid + " to folder: " + node.getPath());
        }
        
        return false;
    }

   void addTranslationIdToDocument(Node node) throws RepositoryException {
        NodeIterator childNodes = node.getNodes();
        String uuid = UUID.randomUUID().toString();
        
        while(childNodes.hasNext()){
            Node childNode = childNodes.nextNode();
            if(childNode.getName().equals(node.getName())){
                checkoutIfCheckedIn(childNode);
                childNode.setProperty("hippotranslation:id", uuid);
                log.debug("set translation uuid: " + uuid + " to document node " + childNode.getPath());
            }
        }
        
    }
  
  void checkoutIfCheckedIn(Node node) throws RepositoryException {
       if(!node.isCheckedOut()){
           node.checkout();
       }
        
    }

  boolean undoUpdate(Node node) {
    throw new UnsupportedOperationException('Updater does not implement undoUpdate method')
  }

}
